,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'function ora_d3_tree_ajax (  p_region in apex_plugin.t_region,',
'                                 p_plugin in apex_plugin.t_plugin )',
'                        return apex_plugin.t_region_ajax_result is',
