'    l_parent_col_no pls_integer;',
'    l_group_col_no  pls_integer;',
'    l_link_col_no   pls_integer;',
'    l_desc_col_no   pls_integer;',
'',
'    l_label  varchar2(4000);',
'    l_value  number;',
