'        ''<div class="a-FlotLine" id="'' || p_region.static_id || ''_container">'' ||',
'            ''<div class="a-FlotLine-desc"></div>'' ||',
'            ''<div class="a-FlotLine-chart" id="'' || p_region.static_id || ''_chart"></div>'' ||',
