,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','COM.ORACLE.APEX.D3.TREEMAP'),'#IMAGE_PREFIX#plugins/com.oracle.apex.d3.treemapchart/1.0/')
