'    sample_pkg.demo_tag_sync(',
'        p_new_tags      => null,',
'        p_old_tags      => :old.tags,',
'        p_content_type  => ''ORDER'',',
'        p_content_id    => :old.order_id );',
'',
'end;',
'/',
'',
'',
'-- Table: DEMO_PRODUCT_INFO',
'CREATE TABLE  "DEMO_PRODUCT_INFO" (    ',
'    "PRODUCT_ID"          NUMBER NOT NULL ENABLE,',
